import java.util.*;class App {public static void main(String[] args) throws Exception {try (Scanner sc = new Scanner(System.in)) {int t = sc.nextInt();String str1, str2 = "";for (int i = 0; i < t; i++) {String str = sc.next();if (str.length() % 2 == 0) {str1 = str.substring(0,str.length() / 2);str2 = str.substring(str.length() / 2,str.length());if (str1.equalsIgnoreCase(str2)) {System.out.println("YES");} else {System.out.println("NO");}}else{System.out.println("NO");}}}}}